import React from 'react';
import Gallery from './Gallery/index'
import Weather from './Weather/index'
import './Holidays.css';
const countryList = require('country-list');

const Holidays = () => {
let randomCountry = '';
// Get random country after clicking
const randomIntNumber = (min, max) => {
  return Math.floor(Math.random() * (max - min + 1) + min)
}
const generateRandomCountry = () => {
  const randomInt = randomIntNumber(1, 150)
  return randomCountry = countryList.getData()[randomInt].name;
}


  return (
    <main className='holidays'>
      <header className='header'>
        <h1 className='header__title'>Are you ready?</h1>
        <button className='header__button' onClick={generateRandomCountry}>TAKE ME THERE!</button>
        <h2 className='header__destination'>Your holidays destination is..</h2>
        <p className='header__country'>{randomCountry}</p>
      </header>
      <Weather />
      <Gallery />
    </main>
  )
}

export default Holidays;
